mapper.select:
select
  resident.id, name, gender, birthday, telephone_number, identity_card, census_register,
  educational_level, party, photo, is_head_of_household, house_id
from housing_info_management.resident,building,unit,house

    building.id = unit.building_id and unit.id = house.unit_id
    and house.id = resident.house_id

        and building.id = #{buildingId}


        and unit_id = #{unitId}


        and house_id = #{houseId}



    limit #{offset}, #{limit}

select *
from resident


        and name like #{name}



    limit #{offset}, #{limit}

select
resident.id, name, gender, birthday, telephone_number, identity_card, census_register,
educational_level, party, photo, is_head_of_household, house_id
from housing_info_management.resident,building,unit,house

    building.id = unit.building_id and unit.id = house.unit_id
    and house.id = resident.house_id
    and is_head_of_household = 1

        and building.id = #{buildingId}


        and unit_id = #{unitId}


        and house_id = #{houseId}

select ( case when a.age is null then '未知' when a.age between 0 and 10 then '0-10岁' when a.age between 10 and 20 then '10-20岁' when a.age between 20 and 30 then '20-30岁' when a.age between 30 and 40 then '30-40岁' when a.age between 40 and 50 then '40-50岁' when a.age between 50 and 60 then '50-60岁' when a.age >= 60 then '60岁以上' end ) as xData, count(*) as count from ( select(YEAR(curdate()) - YEAR(birthday)) as age from resident,building,unit,house
building.id = unit.building_id and unit.id = house.unit_id and house.id = resident.house_id
and building.id = #{buildingId}
and unit.id = #{unitId}
) as a group by xData order by xData

select gender as xData, count(resident.id) as count from resident,building,unit,house
building.id = unit.building_id and unit.id = house.unit_id and house.id = resident.house_id
and building.id = #{buildingId}
and unit.id = #{unitId}
group by xData

select ( case when census_register like '北京%' then '北京' when census_register like '天津%' then '天津' when census_register like '上海%' then '上海' when census_register like '重庆%' then '重庆' when census_register like '河北%' then '河北' when census_register like '黑龙江%' then '黑龙江' when census_register like '吉林%' then '吉林' when census_register like '辽宁%' then '辽宁' when census_register like '山东%' then '山东' when census_register like '江苏%' then '江苏' when census_register like '浙江%' then '浙江' when census_register like '安徽%' then '安徽' when census_register like '福建%' then '福建' when census_register like '江西%' then '江西' when census_register like '广东%' then '广东' when census_register like '广西%' then '广西' when census_register like '海南%' then '海南' when census_register like '河南%' then '河南' when census_register like '湖南%' then '湖南' when census_register like '湖北%' then '湖北' when census_register like '山西%' then '山西' when census_register like '内蒙古%' then '内蒙古' when census_register like '宁夏%' then '宁夏' when census_register like '青海%' then '青海' when census_register like '陕西%' then '陕西' when census_register like '甘肃%' then '甘肃' when census_register like '新疆%' then '新疆' when census_register like '四川%' then '四川' when census_register like '贵州%' then '贵州' when census_register like '云南%' then '云南' when census_register like '西藏%' then '西藏' when census_register like '香港%' then '香港' when census_register like '澳门%' then '澳门' when census_register like '台湾%' then '台湾' else '其他' end ) as name, count(resident.id) as value from resident,building,unit,house
building.id = unit.building_id and unit.id = house.unit_id and house.id = resident.house_id
and building.id = #{buildingId}
and unit.id = #{unitId}
group by census_register

select educational_level as xData, count(resident.id) as count from resident,building,unit,house
building.id = unit.building_id and unit.id = house.unit_id and house.id = resident.house_id
and building.id = #{buildingId}
and unit.id = #{unitId}
group by xData

select party as xData, count(resident.id) as count from resident,building,unit,house
building.id = unit.building_id and unit.id = house.unit_id and house.id = resident.house_id
and building.id = #{buildingId}
and unit.id = #{unitId}
group by xData
